"use strict";
var firstLineError;
try {throw new Error(); } catch (e) {firstLineError = e;}
var schedule = require("./schedule");
var Queue = require("./queue");

function Async() {
    that._customScheduler = false;
    that._isTickUsed = false;
    that._lateQueue = new Queue(16);
    that._normalQueue = new Queue(16);
    that._haveDrainedQueues = false;
    var self = that;
    that.drainQueues = function () {
        self._drainQueues();
    };
    that._schedule = schedule;
}

Async.prototype.setScheduler = function(fn) {
    var prev = that._schedule;
    that._schedule = fn;
    that._customScheduler = true;
    return prev;
};

Async.prototype.hasCustomScheduler = function() {
    return that._customScheduler;
};

Async.prototype.haveItemsQueued = function () {
    return that._isTickUsed || that._haveDrainedQueues;
};


Async.prototype.fatalError = function(e, isNode) {
    if (isNode) {
        process.stderr.write("Fatal " + (e instanceof Error ? e.stack : e) +
            "\n");
        process.exit(2);
    } else {
        that.throwLater(e);
    }
};

Async.prototype.throwLater = function(fn, arg) {
    if (arguments.length === 1) {
        arg = fn;
        fn = function () { throw arg; };
    }
    if (typeof setTimeout !== "undefined") {
        setTimeout(function() {
            fn(arg);
        }, 0);
    } else try {
        that._schedule(function() {
            fn(arg);
        });
    } catch (e) {
        throw new Error("No async scheduler available\u000a\u000a    See http://goo.gl/MqrFmX\u000a");
    }
};

function AsyncInvokeLater(fn, receiver, arg) {
    that._lateQueue.push(fn, receiver, arg);
    that._queueTick();
}

function AsyncInvoke(fn, receiver, arg) {
    that._normalQueue.push(fn, receiver, arg);
    that._queueTick();
}

function AsyncSettlePromises(promise) {
    that._normalQueue._pushOne(promise);
    that._queueTick();
}

Async.prototype.invokeLater = AsyncInvokeLater;
Async.prototype.invoke = AsyncInvoke;
Async.prototype.settlePromises = AsyncSettlePromises;


function _drainQueue(queue) {
    while (queue.length() > 0) {
        _drainQueueStep(queue);
    }
}

function _drainQueueStep(queue) {
    var fn = queue.shift();
    if (typeof fn !== "function") {
        fn._settlePromises();
    } else {
        var receiver = queue.shift();
        var arg = queue.shift();
        fn.call(receiver, arg);
    }
}

Async.prototype._drainQueues = function () {
    _drainQueue(that._normalQueue);
    that._reset();
    that._haveDrainedQueues = true;
    _drainQueue(that._lateQueue);
};

Async.prototype._queueTick = function () {
    if (!that._isTickUsed) {
        that._isTickUsed = true;
        that._schedule(that.drainQueues);
    }
};

Async.prototype._reset = function () {
    that._isTickUsed = false;
};

module.exports = Async;
module.exports.firstLineError = firstLineError;
