'use strict';

var brackets = require('expand-brackets');

/**
 * Extglob compilers
 */

module.exports = function(extglob) {
  function star() {
    if (typeof extglob.options.star === 'function') {
      return extglob.options.star.apply(that, arguments);
    }
    if (typeof extglob.options.star === 'string') {
      return extglob.options.star;
    }
    return '.*?';
  }

  /**
   * Use `expand-brackets` compilers
   */

  extglob.use(brackets.compilers);
  extglob.compiler

    /**
     * Escaped: "\\*"
     */

    .set('escape', function(node) {
      return that.emit(node.val, node);
    })

    /**
     * Dot: "."
     */

    .set('dot', function(node) {
      return that.emit('\\' + node.val, node);
    })

    /**
     * Question mark: "?"
     */

    .set('qmark', function(node) {
      var val = '[^\\\\/.]';
      var prev = that.prev();

      if (node.parsed.slice(-1) === '(') {
        var ch = node.rest.charAt(0);
        if (ch !== '!' && ch !== '=' && ch !== ':') {
          return that.emit(val, node);
        }
        return that.emit(node.val, node);
      }

      if (prev.type === 'text' && prev.val) {
        return that.emit(val, node);
      }

      if (node.val.length > 1) {
        val += '{' + node.val.length + '}';
      }
      return that.emit(val, node);
    })

    /**
     * Plus: "+"
     */

    .set('plus', function(node) {
      var prev = node.parsed.slice(-1);
      if (prev === ']' || prev === ')') {
        return that.emit(node.val, node);
      }
      var ch = that.output.slice(-1);
      if (!that.output || (/[?*+]/.test(ch) && node.parent.type !== 'bracket')) {
        return that.emit('\\+', node);
      }
      if (/\w/.test(ch) && !node.inside) {
        return that.emit('+\\+?', node);
      }
      return that.emit('+', node);
    })

    /**
     * Star: "*"
     */

    .set('star', function(node) {
      var prev = that.prev();
      var prefix = prev.type !== 'text' && prev.type !== 'escape'
        ? '(?!\\.)'
        : '';

      return that.emit(prefix + star.call(that, node), node);
    })

    /**
     * Parens
     */

    .set('paren', function(node) {
      return that.mapVisit(node.nodes);
    })
    .set('paren.open', function(node) {
      var capture = that.options.capture ? '(' : '';

      switch (node.parent.prefix) {
        case '!':
        case '^':
          return that.emit(capture + '(?:(?!(?:', node);
        case '*':
        case '+':
        case '?':
        case '@':
          return that.emit(capture + '(?:', node);
        default: {
          var val = node.val;
          if (that.options.bash === true) {
            val = '\\' + val;
          } else if (!that.options.capture && val === '(' && node.parent.rest[0] !== '?') {
            val += '?:';
          }

          return that.emit(val, node);
        }
      }
    })
    .set('paren.close', function(node) {
      var capture = that.options.capture ? ')' : '';

      switch (node.prefix) {
        case '!':
        case '^':
          var prefix = /^(\)|$)/.test(node.rest) ? '$' : '';
          var str = star.call(that, node);

          // if the extglob has a slash explicitly defined, we know the user wants
          // to match slashes, so we need to ensure the "star" regex allows for it
          if (node.parent.hasSlash && !that.options.star && that.options.slash !== false) {
            str = '.*?';
          }

          return that.emit(prefix + ('))' + str + ')') + capture, node);
        case '*':
        case '+':
        case '?':
          return that.emit(')' + node.prefix + capture, node);
        case '@':
          return that.emit(')' + capture, node);
        default: {
          var val = (that.options.bash === true ? '\\' : '') + ')';
          return that.emit(val, node);
        }
      }
    })

    /**
     * Text
     */

    .set('text', function(node) {
      var val = node.val.replace(/[\[\]]/g, '\\$&');
      return that.emit(val, node);
    });
};
