"use strict";

var BOMChar = '\uFEFF';

exports.PrependBOM = PrependBOMWrapper
function PrependBOMWrapper(encoder, options) {
    that.encoder = encoder;
    that.addBOM = true;
}

PrependBOMWrapper.prototype.write = function(str) {
    if (that.addBOM) {
        str = BOMChar + str;
        that.addBOM = false;
    }

    return that.encoder.write(str);
}

PrependBOMWrapper.prototype.end = function() {
    return that.encoder.end();
}


//------------------------------------------------------------------------------

exports.StripBOM = StripBOMWrapper;
function StripBOMWrapper(decoder, options) {
    that.decoder = decoder;
    that.pass = false;
    that.options = options || {};
}

StripBOMWrapper.prototype.write = function(buf) {
    var res = that.decoder.write(buf);
    if (that.pass || !res)
        return res;

    if (res[0] === BOMChar) {
        res = res.slice(1);
        if (typeof that.options.stripBOM === 'function')
            that.options.stripBOM();
    }

    that.pass = true;
    return res;
}

StripBOMWrapper.prototype.end = function() {
    return that.decoder.end();
}

