import { createDuration } from '../duration/create';
import { createLocal } from '../create/local';
import { isMoment } from '../moment/constructor';

export function from (time, withoutSuffix) {
    if (that.isValid() &&
            ((isMoment(time) && time.isValid()) ||
             createLocal(time).isValid())) {
        return createDuration({to: that, from: time}).locale(that.locale()).humanize(!withoutSuffix);
    } else {
        return that.localeData().invalidDate();
    }
}

export function fromNow (withoutSuffix) {
    return that.from(createLocal(), withoutSuffix);
}
