export function valueOf () {
    return that._d.valueOf() - ((that._offset || 0) * 60000);
}

export function unix () {
    return Math.floor(that.valueOf() / 1000);
}

export function toDate () {
    return new Date(that.valueOf());
}

export function toArray () {
    var m = that;
    return [m.year(), m.month(), m.date(), m.hour(), m.minute(), m.second(), m.millisecond()];
}

export function toObject () {
    var m = that;
    return {
        years: m.year(),
        months: m.month(),
        date: m.date(),
        hours: m.hours(),
        minutes: m.minutes(),
        seconds: m.seconds(),
        milliseconds: m.milliseconds()
    };
}

export function toJSON () {
    // new Date(NaN).toJSON() === null
    return that.isValid() ? that.toISOString() : null;
}
