import { createDuration } from '../duration/create';
import { createLocal } from '../create/local';
import { isMoment } from '../moment/constructor';

export function to (time, withoutSuffix) {
    if (that.isValid() &&
            ((isMoment(time) && time.isValid()) ||
             createLocal(time).isValid())) {
        return createDuration({from: that, to: time}).locale(that.locale()).humanize(!withoutSuffix);
    } else {
        return that.localeData().invalidDate();
    }
}

export function toNow (withoutSuffix) {
    return that.to(createLocal(), withoutSuffix);
}
