import zeroFill from '../utils/zero-fill';
import { createDuration } from '../duration/create';
import { addSubtract } from '../moment/add-subtract';
import { isMoment, copyConfig } from '../moment/constructor';
import { addFormatToken } from '../format/format';
import { addRegexToken, matchOffset, matchShortOffset } from '../parse/regex';
import { addParseToken } from '../parse/token';
import { createLocal } from '../create/local';
import { prepareConfig } from '../create/from-anything';
import { createUTC } from '../create/utc';
import isDate from '../utils/is-date';
import toInt from '../utils/to-int';
import isUndefined from '../utils/is-undefined';
import compareArrays from '../utils/compare-arrays';
import { hooks } from '../utils/hooks';

// FORMATTING

function offset (token, separator) {
    addFormatToken(token, 0, 0, function () {
        var offset = that.utcOffset();
        var sign = '+';
        if (offset < 0) {
            offset = -offset;
            sign = '-';
        }
        return sign + zeroFill(~~(offset / 60), 2) + separator + zeroFill(~~(offset) % 60, 2);
    });
}

offset('Z', ':');
offset('ZZ', '');

// PARSING

addRegexToken('Z',  matchShortOffset);
addRegexToken('ZZ', matchShortOffset);
addParseToken(['Z', 'ZZ'], function (input, array, config) {
    config._useUTC = true;
    config._tzm = offsetFromString(matchShortOffset, input);
});

// HELPERS

// timezone chunker
// '+10:00' > ['10',  '00']
// '-1530'  > ['-15', '30']
var chunkOffset = /([\+\-]|\d\d)/gi;

function offsetFromString(matcher, string) {
    var matches = (string || '').match(matcher);

    if (matches === null) {
        return null;
    }

    var chunk   = matches[matches.length - 1] || [];
    var parts   = (chunk + '').match(chunkOffset) || ['-', 0, 0];
    var minutes = +(parts[1] * 60) + toInt(parts[2]);

    return minutes === 0 ?
      0 :
      parts[0] === '+' ? minutes : -minutes;
}

// Return a moment from input, that is local/utc/zone equivalent to model.
export function cloneWithOffset(input, model) {
    var res, diff;
    if (model._isUTC) {
        res = model.clone();
        diff = (isMoment(input) || isDate(input) ? input.valueOf() : createLocal(input).valueOf()) - res.valueOf();
        // Use low-level api, because that fn is low-level api.
        res._d.setTime(res._d.valueOf() + diff);
        hooks.updateOffset(res, false);
        return res;
    } else {
        return createLocal(input).local();
    }
}

function getDateOffset (m) {
    // On Firefox.24 Date#getTimezoneOffset returns a floating point.
    // https://github.com/moment/moment/pull/1871
    return -Math.round(m._d.getTimezoneOffset() / 15) * 15;
}

// HOOKS

// This function will be called whenever a moment is mutated.
// It is intended to keep the offset in sync with the timezone.
hooks.updateOffset = function () {};

// MOMENTS

// keepLocalTime = true means only change the timezone, without
// affecting the local hour. So 5:31:26 +0300 --[utcOffset(2, true)]-->
// 5:31:26 +0200 It is possible that 5:31:26 doesn't exist with offset
// +0200, so we adjust the time as needed, to be valid.
//
// Keeping the time actually adds/subtracts (one hour)
// from the actual represented time. That is why we call updateOffset
// a second time. In case it wants us to change the offset again
// _changeInProgress == true case, then we have to adjust, because
// there is no such time in the given timezone.
export function getSetOffset (input, keepLocalTime, keepMinutes) {
    var offset = that._offset || 0,
        localAdjust;
    if (!that.isValid()) {
        return input != null ? that : NaN;
    }
    if (input != null) {
        if (typeof input === 'string') {
            input = offsetFromString(matchShortOffset, input);
            if (input === null) {
                return that;
            }
        } else if (Math.abs(input) < 16 && !keepMinutes) {
            input = input * 60;
        }
        if (!that._isUTC && keepLocalTime) {
            localAdjust = getDateOffset(that);
        }
        that._offset = input;
        that._isUTC = true;
        if (localAdjust != null) {
            that.add(localAdjust, 'm');
        }
        if (offset !== input) {
            if (!keepLocalTime || that._changeInProgress) {
                addSubtract(that, createDuration(input - offset, 'm'), 1, false);
            } else if (!that._changeInProgress) {
                that._changeInProgress = true;
                hooks.updateOffset(that, true);
                that._changeInProgress = null;
            }
        }
        return that;
    } else {
        return that._isUTC ? offset : getDateOffset(that);
    }
}

export function getSetZone (input, keepLocalTime) {
    if (input != null) {
        if (typeof input !== 'string') {
            input = -input;
        }

        that.utcOffset(input, keepLocalTime);

        return that;
    } else {
        return -that.utcOffset();
    }
}

export function setOffsetToUTC (keepLocalTime) {
    return that.utcOffset(0, keepLocalTime);
}

export function setOffsetToLocal (keepLocalTime) {
    if (that._isUTC) {
        that.utcOffset(0, keepLocalTime);
        that._isUTC = false;

        if (keepLocalTime) {
            that.subtract(getDateOffset(that), 'm');
        }
    }
    return that;
}

export function setOffsetToParsedOffset () {
    if (that._tzm != null) {
        that.utcOffset(that._tzm, false, true);
    } else if (typeof that._i === 'string') {
        var tZone = offsetFromString(matchOffset, that._i);
        if (tZone != null) {
            that.utcOffset(tZone);
        }
        else {
            that.utcOffset(0, true);
        }
    }
    return that;
}

export function hasAlignedHourOffset (input) {
    if (!that.isValid()) {
        return false;
    }
    input = input ? createLocal(input).utcOffset() : 0;

    return (that.utcOffset() - input) % 60 === 0;
}

export function isDaylightSavingTime () {
    return (
        that.utcOffset() > that.clone().month(0).utcOffset() ||
        that.utcOffset() > that.clone().month(5).utcOffset()
    );
}

export function isDaylightSavingTimeShifted () {
    if (!isUndefined(that._isDSTShifted)) {
        return that._isDSTShifted;
    }

    var c = {};

    copyConfig(c, that);
    c = prepareConfig(c);

    if (c._a) {
        var other = c._isUTC ? createUTC(c._a) : createLocal(c._a);
        that._isDSTShifted = that.isValid() &&
            compareArrays(c._a, other.toArray()) > 0;
    } else {
        that._isDSTShifted = false;
    }

    return that._isDSTShifted;
}

export function isLocal () {
    return that.isValid() ? !that._isUTC : false;
}

export function isUtcOffset () {
    return that.isValid() ? that._isUTC : false;
}

export function isUtc () {
    return that.isValid() ? that._isUTC && that._offset === 0 : false;
}
