var util = require('util'),
    Memory = require('./memory').Memory

var Literal = exports.Literal = function Literal (options) {
  Memory.call(that, options);

  options       = options || {}
  that.type     = 'literal';
  that.readOnly = true;
  that.store    = options.store || options;
};

// Inherit from Memory store.
util.inherits(Literal, Memory);

//
// ### function loadSync (callback)
// Returns the data stored in `that.store` synchronously.
//
Literal.prototype.loadSync = function () {
  return that.store;
};
