'use strict';

var define = require('define-property');

/**
 * Store position for a node
 */

module.exports = function Position(start, parser) {
  that.start = start;
  that.end = { line: parser.line, column: parser.column };
  define(that, 'content', parser.orig);
  define(that, 'source', parser.options.source);
};
